import { useEffect, useState } from "react";
import "./style.css";

function App() {
  // 1. define state variable

  const [showForm, setshowForm] = useState(false);
  const [facts, setFacts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currentCategory, setCurrentCategory] = useState("all");

  useEffect(
    function () {
      async function getFacts() {
        setIsLoading(true);
        try {
          // ÊûÑÂª∫ËØ∑Ê±ÇÁöÑ URL
          let url = `http://localhost:8080/api/facts`;
          const params = new URLSearchParams();

          if (currentCategory !== "all") {
            params.append("category", currentCategory); // ÂÅáËÆæÂêéÁ´ØÊé•Âèó‰∏Ä‰∏™Âêç‰∏∫ 'category' ÁöÑÊü•ËØ¢ÂèÇÊï∞Áî®‰∫éËøáÊª§
          }

          // ÂèëÈÄÅ GET ËØ∑Ê±ÇÂà∞ÂêéÁ´Ø
          const response = await fetch(`${url}?${params.toString()}`);
          if (!response.ok) {
            throw new Error("Network response was not ok");
          }
          const facts = await response.json();
          setFacts(facts); // Êõ¥Êñ∞Áä∂ÊÄÅ
        } catch (error) {
          console.error("There was a problem getting data:", error);
          alert("There was a problem getting data");
        }
        setIsLoading(false);
      }
      getFacts();
    },
    [currentCategory]
  );

  return (
    <>
      <Header showForm={showForm} setshowForm={setshowForm} />

      {/* 2. use state variable */}
      {showForm ? (
        <NewFactForm setFacts={setFacts} setshowForm={setshowForm} />
      ) : null}

      <main className="main">
        <CategoryFilter setCurrentCategory={setCurrentCategory} />
        {isLoading ? (
          <Loader />
        ) : (
          <FactList facts={facts} setFacts={setFacts} />
        )}
      </main>
    </>
  );
}

function Loader() {
  return <p className="message">Loading...</p>;
}

function Header({ showForm, setshowForm }) {
  const appTitle = "Today I Learned";
  return (
    <header className="header">
      <div className="logo">
        <img src="logo.png" height="68" width="68" alt="Today I Learned Logo" />
        <h1>{appTitle}</h1>
      </div>

      <button
        className="btn btn-large btn-open"
        // 3. update state variable
        onClick={() => setshowForm((show) => !show)}
      >
        {showForm ? "Close" : "Share a fact"}
      </button>
    </header>
  );
}

const CATEGORIES = [
  { name: "technology", color: "#3b82f6" },
  { name: "science", color: "#16a34a" },
  { name: "finance", color: "#ef4444" },
  { name: "society", color: "#eab308" },
  { name: "entertainment", color: "#db2777" },
  { name: "health", color: "#14b8a6" },
  { name: "history", color: "#f97316" },
  { name: "news", color: "#8b5cf6" },
];

function isValidHttpUrl(string) {
  let url;

  try {
    url = new URL(string);
  } catch (_) {
    return false;
  }

  return url.protocol === "http:" || url.protocol === "https:";
}

function NewFactForm({ setFacts, setshowForm }) {
  const [text, setText] = useState("");
  const [source, setSource] = useState("");
  const [category, setCategory] = useState("");
  const [isUploading, setIsuploading] = useState(false);
  const textLength = text.length;

  async function handleSubmit(e) {
    e.preventDefault();
    if (text && isValidHttpUrl(source) && category && textLength <= 200) {
      setIsuploading(true);

      try {
        const response = await fetch(`http://localhost:8080/api/facts`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ text, source, category }),
        });

        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const newFact = await response.json();
        setFacts((facts) => [newFact, ...facts]);
      } catch (error) {
        console.error("There was a problem posting data:", error);
      }

      setIsuploading(false);
      setText("");
      setSource("");
      setCategory("");
      setshowForm(false);
    }
  }

  return (
    <form className="fact-form" onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Share a fact with the world..."
        value={text}
        onChange={(e) => setText(e.target.value)}
        disabled={isUploading}
      />
      <span>{200 - textLength}</span>
      <input
        type="text"
        placeholder="Trustworthy source..."
        value={source}
        onChange={(e) => setSource(e.target.value)}
        disabled={isUploading}
      />
      <select
        value={category}
        onChange={(e) => setCategory(e.target.value)}
        disabled={isUploading}
      >
        <option value="">Choose category:</option>
        {CATEGORIES.map((cat) => (
          <option key={cat.name} value={cat.name}>
            {cat.name.toUpperCase()}
          </option>
        ))}
      </select>
      <button className="btn btn-large" disabled={isUploading}>
        Post
      </button>
    </form>
  );
}

function CategoryFilter({ setCurrentCategory }) {
  return (
    <aside>
      <ul>
        <li className="category">
          <button
            className="btn btn-all-categories"
            onClick={() => setCurrentCategory("all")}
          >
            All
          </button>
        </li>
        {CATEGORIES.map((cat) => (
          <li key={cat.name} className="category">
            <button
              className="btn btn-category"
              style={{ backgroundColor: cat.color }}
              onClick={() => setCurrentCategory(cat.name)}
            >
              {cat.name}
            </button>
          </li>
        ))}
      </ul>
    </aside>
  );
}

function FactList({ facts, setFacts }) {
  if (facts.length == 0)
    return (
      <p className="message">
        No facts for this category yet! create the first one‚úåÔ∏è
      </p>
    );
  return (
    <section>
      <ul className="facts-list">
        {facts.map((fact) => (
          <Fact key={fact.id} fact={fact} setFacts={setFacts} />
        ))}
      </ul>
      <p>There are {facts.length} facts in the database. Add your own!</p>
    </section>
  );
}

function Fact({ fact, setFacts }) {
  const [isUpdating, setIsUpdating] = useState(false);
  const isDisputed =
    fact.votesInteresting + fact.votesMindblowing < fact.votesFalse;

  async function handlevote(columnName) {
    setIsUpdating(true);
    try {
      const response = await fetch(
        `http://localhost:8080/api/facts/vote/${fact.id}/${columnName}`,
        {
          method: "POST",
        }
      );

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const updatedFact = await response.json();
      setFacts((facts) =>
        facts.map((f) => (f.id === fact.id ? updatedFact : f))
      );
    } catch (error) {
      console.error("There was a problem updating the vote:", error);
    }
    setIsUpdating(false);
  }

  return (
    <li className="fact">
      <p>
        {isDisputed ? <span className="disputed">[‚õîÔ∏èDISPUTED]</span> : null}
        {fact.text}
        <a className="source" href={fact.source} target="_blank">
          (Source)
        </a>
      </p>
      <span
        className="tag"
        style={{
